export default {
  name: "dialogfund",
  props: {
    dialog: Object,
    formData: Object
  },
  data: function data() {
    return {
      format_type_list: ["提现", "提现手续费", "充值", "优惠券", "充值礼券", "转账"],
      rules: {
        type: [{
          required: true,
          message: "请选择类型",
          rigger: "blur"
        }],
        describe: [{
          required: true,
          message: "请输入收支描述",
          rigger: "blur"
        }],
        income: [{
          required: true,
          message: "请输入收入",
          rigger: "blur"
        }],
        expend: [{
          required: true,
          message: "请输入支出",
          rigger: "blur"
        }],
        cash: [{
          required: true,
          message: "请输入现金",
          rigger: "blur"
        }],
        remark: [{
          rigger: "blur"
        }]
      }
    };
  },
  methods: {
    submitForm: function submitForm(formName) {
      var _this = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          var url = _this.dialog.option == "add" ? "add" : "edit/".concat(_this.formData.id); //添加数据

          _this.$axios.post("/api/profiles/".concat(url), _this.formData).then(function (res) {
            if (url == "add") {
              _this.$message({
                message: "数据添加成功",
                type: "success"
              });
            } else {
              _this.$message({
                message: "数据编辑成功",
                type: "success"
              });
            } //隐藏dialog


            _this.dialog.show = false; //让父组件刷新

            _this.$emit("update");
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
