export default {
  name: "dialogfund",
  props: {
    dialog: Object,
    formData: Object
  },
  data() {
    return {
      format_type_list: [
        "提现",
        "提现手续费",
        "充值",
        "优惠券",
        "充值礼券",
        "转账"
      ],
      rules: {
        type: [{ required: true, message: "请选择类型", rigger: "blur" }],
        describe: [
          { required: true, message: "请输入收支描述", rigger: "blur" }
        ],
        income: [{ required: true, message: "请输入收入", rigger: "blur" }],
        expend: [{ required: true, message: "请输入支出", rigger: "blur" }],
        cash: [{ required: true, message: "请输入现金", rigger: "blur" }],
        remark: [{ rigger: "blur" }]
      }
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          const url = this.dialog.option == "add" ? "add" : `edit/${this.formData.id}`;
          //添加数据
          this.$axios.post(`/api/profiles/${url}`, this.formData).then(res => {
            if (url == "add") {
              this.$message({
                message: "数据添加成功",
                type: "success"
              });
            } else {
              this.$message({
                message: "数据编辑成功",
                type: "success"
              });
            }
            //隐藏dialog
            this.dialog.show = false;
            //让父组件刷新
            this.$emit("update");
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
